    private void bttCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttCalcularActionPerformed
        // TODO add your handling code here:
        String nombre, numero;
        double patrimonio,estrato;
        
        nombre = txtNombre.getText();
        numero = txtNumero.getText();
        patrimonio = Double.parseDouble(txtPatrimonio.getText());
        estrato = Double.parseDouble(txtEstrato.getText());
        
        Estudiante a = new Estudiante(nombre, numero, patrimonio, estrato);
        
        txtSalida.setText("El estudiante con numero de inscripcion "+numero+" y nombre "+nombre+ " debe pagar $"+a.calcularMatricula());
    }//GEN-LAST:event_bttCalcularActionPerformed

